// Get features which appear in more than one document of the cluster
var extractClusterFeatures = function (cluster) {
    var featureList = {};

    // Fill feature list with words found in the documents of the cluster
    cluster.documents.forEach(function (doc) {
        for (var word in doc.data) {
            if (doc.data[word] > 0) {
                if (featureList[word]) {
                    //featureList[word] += doc.data[word];
                    featureList[word].count++;
                } else {
                    featureList[word] = {
                        count: 1,
                        word: doc.originalReference[word]
                    };
                }
            }
        }
    });

    // Remove feature if it only appears in one document
    for (var word in featureList) {
        if (featureList[word].count == 1 || !featureList[word].word) {
            delete featureList[word];
        }
    }

    return featureList;
};

//Converts an array vector to csv row
function ConvertToCSV(array) {
    var str = "";

    for (var i in array) {
        if (array.hasOwnProperty(i)) {
            str += array[i] + ',';
        }
    }

    /*Object.keys(array).map(function(key,i) {
          return i;
    });*/

    str = str.substring(0, str.length - 1);

    return str;
}

function csvHelper(results) {
    var stringResult = "";
    for (var i = 0; i < results.length; i++) {
        var tmpVector = Array();
        var cResultData = results[i].data;

        for (var attrib in cResultData) {
            if (cResultData.hasOwnProperty(attrib)) {
                tmpVector[tmpVector.length] = cResultData[attrib];
            }
        }
        stringResult += ConvertToCSV(tmpVector) + "\n";
    }
    //console.log(stringResult);
}

function shuffle(array) {
    var currentIndex = array.length, temporaryValue, randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

        // Pick a remaining element...
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex -= 1;

        // And swap it with the current element.
        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue;
    }

    return array;
}

function order_results(results) {
    for (var i = 0; i < results.length; i++) {
        results[i].strIdx = (i + 1) + "";
    }

    function compare(a, b) {
        if (a.strIdx < b.strIdx)
            return -1;
        else if (a.strIdx > b.strIdx)
            return 1;
        else
            return 0;
    }

    results.sort(compare);

    return results;
}